import { useForm } from 'react-hook-form';
import { useState } from 'react';
import styled from 'styled-components';
import useCurrentLocation from '../utils/useCurrentLocation';
import TagAutoComplete from '../components/TagAutoComplete';
import KakaoMapClick from '../components/KakaoMapClick';

enum GenderEnum {
  female = '여성',
  male = '남성',
}

enum AgeEnum {
  teenage = '10대',
  twenties = '20대',
  thirties = '30대',
  forties = '40대',
  fifties = '50대',
  sixties = '60대',
}

interface IFormInput {
  tag: string;
  title: string;
  content: string;
  date: string;
  quota: number;
  location: string;
  genderCondition: GenderEnum;
  ageCondition: AgeEnum;
  heartRateCondition: number;
  image: string;
}

const CRContainer = styled.div`
  background-color: var(--gray);
  color: white;
  display: flex;
  justify-content: center;
  margin-top: 5rem;
  height: 100%;
  font-size: 16px;
`;

const CRForm = styled.form`
  width: 35rem;
  height: auto;
  border: 0.05rem solid white;
  border-radius: 1rem;
  padding: 1rem;
  margin-top: 1.7rem;
  margin-bottom: 1.7rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  > div:first-child {
    margin-top: 1rem;
    margin-bottom: 1.5rem;
    font-weight: bold;
  }

  > div:not(:nth-child(2), :nth-child(7)) {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 20rem;
    margin: 0.5rem;
    margin-bottom: 1rem;

    label,
    p {
      width: 6rem;
    }

    .length {
      height: 5rem;
    }

    input,
    textarea,
    select {
      width: 15rem;
      outline: none;
      border: none;
      background-color: rgba(1, 1, 1, 0);
      border-bottom: 0.1rem solid grey;
      color: white;
      &:focus-within {
        border-bottom: 0.1rem solid white;
      }
    }

    > div {
      label {
        width: 3rem;
      }
      input {
        width: 1rem;
      }
      width: 8rem;
    }
  }

  > div:nth-child(2) {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 0.5rem;
    width: 20rem;
    label {
      width: 6rem;
      white-space: nowrap;
      margin-right: 1.5rem;
    }
    > div {
      width: 100%;
      margin-bottom: 0.5rem;
      display: flex;
      input {
        height: auto;
      }
    }
  }

  > div:nth-child(7) {
    display: flex;
    justify-content: center;
    align-items: center;

    label {
      width: 4rem;
      margin-right: 1rem;
    }
  }

  .submitBtn {
    width: 6rem;
    text-decoration: none;
    background-color: var(--gray);
    color: white;
    border-radius: 0.2rem;
    margin: 0.3rem;
    padding: 0.5rem 1rem;
    transition: 0.2s ease-in-out;
    font-size: 16px;
    &:hover {
      cursor: pointer;
      background-color: var(--neon-yellow);
      color: black;
      transition: 0.2s ease-in-out;
    }
  }

  .heartCon {
    width: 15rem;
    margin: 0.5rem;
    input {
      width: 13rem;
    }
  }

  .ageCon {
    width: 15rem;
    display: flex;
    flex-wrap: wrap;
    margin: 0.5rem;
    span {
      width: 5rem;
    }
  }

  .mapClick {
    width: 15rem;
    height: 15rem;
  }
`;

const CreateRecruit = () => {
  const { register, handleSubmit } = useForm<IFormInput>();
  const onSubmit = (data: IFormInput) => console.log(data);
  const [value, setValue] = useState(60);
  const [location, setLocation] = useState<{
    latitude: number;
    longitude: number;
  } | null>(null);

  useCurrentLocation().then((res) => {
    if (res === undefined) return;
    setLocation(res);
  });

  const handleChange = (e: any) => {
    setValue(e.target.value);
  };

  const [filterTag, setFilterTag] = useState('');

  return (
    <CRContainer>
      <CRForm onSubmit={handleSubmit(onSubmit)}>
