import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import classifyingStatus from '../utils/classifyingStatus';
import Button from './Button';
import maskingNickname from '../utils/maskingNickname';

const SelectContainer = styled.div`
  display: flex;
  > div:nth-child(2) {
    margin: 0px 20px;
  }
  margin-bottom: 40px;
`;

const SelectBox = styled.div`
  width: 100%;
  height: 200px;
  border: 1px solid white;
  border-radius: 20px;
  > h3 {
    margin-top: -10px;
    text-align: center;
    width: 100%;
    > span {
      background-color: var(--gray);
      padding: 20px;
    }
  }
  ul {
    padding-right: 20px;
    line-height: 130%;
    font-size: 100%;
    li {
      margin-bottom: 10px;
    }
  }
`;

const ApplicantsBox = styled.div`
  height: 170px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 20px;
  padding-top: 0px;
  > div:nth-child(1) {
    font-size: 120%;
    font-weight: bold;
    i {
      margin-right: 10px;
    }
  }
  > h2 {
    margin-bottom: 10px;
    span {
      font-size: 1rem;
      margin-left: 10px;
      font-weight: normal;
    }
  }
  > div:last-child {
    margin-top: 10px;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
  }
`;

const ProfileImg = styled.div`
  position: relative;
  img {
    border-radius: 50%;
    &:hover {
      + div {
        display: block;
      }
    }
  }
`;

const Bubble = styled.div`
  display: none;
  position: absolute;
  top: -140%;
  left: 50%;
  transform: translate(-50%, 0%);
  .bubble {
    position: relative;
    background: #ffffff;
    color: #000000;
    font-family: Arial;
    font-size: 14px;
    box-shadow: 0px 0px 20px 10px rgba(163, 163, 163, 0.42);
    text-align: center;
    border-radius: 5px;
    padding: 5px 7px;
  }
  .bubble:after {
    content: '';
    position: absolute;
    display: block;
    width: 0;
    z-index: 1;
    border-style: solid;
    border-color: #ffffff transparent;
    border-width: 5px 5px 0;
    bottom: -5px;
    left: 50%;
    margin-left: -5px;
  }
`;

const ApplyBox = styled.div`
  height: 170px;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
  text-align: center;
  padding: 20px;
  padding-top: 0px;
  > div {
    font-size: 120%;
    word-break: keep-all;
    line-height: 150%;
  }
  > button {
    font-size: 150%;
    font-weight: bold;
  }
`;

interface ApplyConditionProps {
  applicantsId: number[];
  heartCond: number;
  ageGroup: string[];
  sexCon: 'Male' | 'Female' | 'Both';
  recruitStatus: '모집중' | '최소인원충족' | '모집완료' | '활동종료';
  creatorId: number;
  applies: { memberId: number; nickname: string; heart: number }[];
  minRequire: number;
  require: number;
}

const RecruitApplyBeforeMeeting = ({
  applicantsId,
  heartCond,
  ageGroup,
  sexCon,
  recruitStatus,
  creatorId,
  applies,
  minRequire,
  require,
}: ApplyConditionProps) => {
  const LOGIN_INFO = {
    memberId: 11,
    heart: 50,
    birth: '1995.01.11',
    sex: 'Male',
  };

  const { recruitId } = useParams();

  const calculateAge = (d: string) => {
    const TODAY = new Date();
    const BIRTHDATE = new Date(d);
    let AGE = TODAY.getFullYear() - BIRTHDATE.getFullYear();
    const M = TODAY.getMonth() - BIRTHDATE.getMonth();
    if (M < 0 || (M === 0 && TODAY.getDate() < BIRTHDATE.getDate())) {
      AGE -= 1;
    }
    return `${Math.floor(AGE / 10)}0`;
  };

  const isValidForApply = () => {
    if (recruitStatus === '모집완료') {
      return '정원이 초과되었습니다';
    }
    if (sexCon !== LOGIN_INFO.sex && sexCon !== 'Both') {
      return '성별조건에 부합하지 않습니다';
    }
    if (heartCond > LOGIN_INFO.heart) {
      return '심박수조건에 부합하지 않습니다';
    }
    if (!ageGroup.includes(calculateAge(LOGIN_INFO.birth))) {
      return '연령조건에 부합하지 않습니다';
    }
    return true;
  };

  return (
    <SelectContainer>
      <SelectBox>
        <h3>
          <span>모집인원</span>
        </h3>
        <ApplicantsBox>
          <div style={{ color: classifyingStatus(recruitStatus).color }}>
            <i className={classifyingStatus(recruitStatus).icon} />
            {recruitStatus}
          </div>
          <h2>
            {`${applies.length}`}
            <span>{`/${require}명`}</span>
          </h2>
          <div>{`(최소인원 ${minRequire}명)`}</div>
          <div>
            {applies.map((el) => (
              <ProfileImg>
                <img
                  src={`https://picsum.photos/seed/${el.memberId}/20/20.webp`}
                  alt={`avatar of ${maskingNickname(el.nickname)}`}
                />
                <Bubble>
                  <div className="bubble">{maskingNickname(el.nickname)}</div>
                </Bubble>
              </ProfileImg>
            ))}
          </div>
        </ApplicantsBox>
        <div />
      </SelectBox>
      <SelectBox>
        <h3>
          <span>모집신청</span>
        </h3>
        {creatorId === LOGIN_INFO.memberId ? (
          <ApplyBox>
            <div>자기 자신이 쓴 글에는 신청하실 수 없습니다</div>
            <Button value="신청 불가" disabled onClick={() => {}} />
          </ApplyBox>
        ) : (
          <div>
            {!applicantsId.includes(LOGIN_INFO.memberId) ? (
              <ApplyBox>
                <div>
                  {isValidForApply() === true
                    ? '모임에 참여하시겠습니까?'
                    : `${isValidForApply()}`}
                </div>
                <Button
                  value="신청"
                  disabled={isValidForApply() !== true}
                  onClick={() => {
                    console.log(
                      `PATCH /recruits/${recruitId}/application 신청!`,
                    );
                  }}
                />
              </ApplyBox>
            ) : (
              <ApplyBox>
                <div>
                  {recruitStatus !== '모집완료'
                    ? '모임 참여를 취소하시겠습니까?'
                    : '모집완료가 된 이후에는 취소하실 수 없습니다'}
                </div>
                <Button
                  value="취소"
                  disabled={recruitStatus === '모집완료'}
                  onClick={() => {
                    console.log(
                      `PATCH /recruits/${recruitId}/application 취소!`,
                    );
                  }}
                />
              </ApplyBox>
            )}
          </div>
        )}
