//   font-size: 16px;
//   white-space: nowrap;
//   &:hover {
//     cursor: pointer;
//     background-color: var(--neon-yellow);
//     color: black;
//     transition: 0.2s ease-in-out;
//   }
// `;

const Board = styled.nav`
  color: white;
  display: flex;
  align-items: center;
  margin-top: 0.5rem;
  a {
    text-decoration: none;
    color: white;
    font-size: 100%;
    transition: 0.2s ease-in-out;
    width: auto;
    height: 1rem;
    margin: 0 0.3rem;
    margin-right: 15px;
    white-space: nowrap;
    display: flex;
    align-items: flex-start;
    &:hover {
      color: var(--neon-yellow);
      text-shadow: white 0 0 1rem;
      font-weight: bold;
      transition: 0.2s ease-in-out;
    }
  }
`;

const Header = () => (
  <HeaderContainer>
    <div>
      <Logo to="/">
        <i className="fa-solid fa-heart-pulse" />
        HEART
      </Logo>
      <Board>
        <Link to="/freeboard">자유게시판</Link>
        <Link to="/freeboard/tags">자유게시판 태그</Link>
        <Link to="/recruits">모집게시판</Link>
        <Link to="/recruits/tags">모집게시판 태그</Link>
      </Board>
    </div>
    <ButtonsContainer>
      <form>
        <i className="fa-solid fa-magnifying-glass" />
        <input placeholder="Search here..." />
      </form>
      <ButtonLink value="로그인" to="/login" />
      <ButtonLink value="회원가입" to="/signup" />
    </ButtonsContainer>
  </HeaderContainer>
);

export default Header;
