import { useForm } from 'react-hook-form';
import { useState } from 'react';
import styled from 'styled-components';
import AutoCompleteForArray from '../components/AutoCompleteForArray';
import useCurrentLocation from '../utils/useCurrentLocation';
import KakaoMapClick from '../components/KakaoMapClick';
import Button from '../components/Button';

enum GenderEnum {
  female = 'Ïó¨ÏÑ±',
  male = 'ÎÇ®ÏÑ±',
  both = 'ÏÑ±Î≥Ñ Î¨¥Í¥Ä',
}

enum AgeEnum {
  teenage = '10ÎåÄ',
  twenties = '20ÎåÄ',
  thirties = '30ÎåÄ',
  forties = '40ÎåÄ',
  fifties = '50ÎåÄ',
  sixties = '60ÎåÄ',
}

interface IFormInput {
  tag: string;
  title: string;
  content: string;
  date: string;
  quota: number;
  location: string;
  genderCondition: GenderEnum;
  ageCondition: AgeEnum;
  heartRateCondition: number;
  // image: string;
}

const CRContainer = styled.div`
  background-color: var(--gray);
  color: white;
  display: flex;
  justify-content: center;
  margin-top: 5rem;
  height: 100%;
  font-size: 16px;
`;

const CRForm = styled.form`
  width: 35rem;
  height: auto;
  border: 0.05rem solid white;
  border-radius: 1rem;
  padding: 1rem;
  margin-top: 1.7rem;
  margin-bottom: 1.7rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  > div {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 1rem;
    label,
    p {
      width: 6rem;
    }
    input,
    textarea,
    select {
      margin-bottom: 0.5rem;
      width: 15rem;
      /* margin: 0.5rem; */
      outline: none;
      border: none;
      background-color: rgba(1, 1, 1, 0);
      border-bottom: 0.1rem solid grey;
      color: white;
      &:focus-within {
        border-bottom: 0.1rem solid white;
      }
    }
  }
`;

const TagContainer = styled.div`
  > div {
    width: 15rem;
  }
`;

const MapContainer = styled.div`
  .mapClick {
    width: 15rem;
    height: 15rem;
  }
`;
const AgeContainer = styled.div`
  p {
    width: 6rem;
  }
  div {
    width: 15rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    > div {
      display: flex;
      flex-direction: row;
      justify-content: center;
      align-items: center;
      input {
        width: 5rem;
      }
      label {
        width: 5rem;
      }
    }
  }
`;

const HeartContainer = styled.div`
  > div {
    width: 15rem;
    input {
      width: 12rem;
      margin-right: 0.5rem;
    }
