import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import styled from 'styled-components';
import { useForm } from 'react-hook-form';
import { useParams, Link, Navigate, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import Tag from '../components/Tag';
import KakaoMap from '../components/KakaoMap';
import useCurrentLocation from '../utils/useCurrentLocation';
// declare global {
//   interface Window {
//     kakao: any;
//   }
//   const kakao: any;
// }

const EditContainer = styled.form`
  background-color: var(--gray);
  color: white;
  display: flex;
  justify-content: center;
  height: 100%;
  padding: 10rem;
`;

const Container = styled.div`
  margin: 1rem;
  display: flex;
  flex-direction: column;
  > div:first-child {
    text-shadow: white 0 0 3px;
    font-size: 24px;
    margin: 20px;
    text-align: center;
  }
  > span {
    width: 100%;
    display: flex;
    justify-content: center;
  }
`;

const PersonalInfo = styled.div`
  border: 2px solid white;
  padding: 10px;
  margin: 10px 0 10px 10px;
  border-radius: 20px;
  padding: 40px 0px 40px 20px;
  input {
    margin-bottom: 15px;
    background-color: var(--gray);
    padding: 5px;
    margin-left: 10px;
    font-size: 16px;
    border: none;
    border-bottom: 2px solid gray;
    width: 13rem;
    outline: none;
    color: white;
    &:focus-within {
      border-bottom: 2px solid white;
      transition: 0.2s ease-in-out;
    }
    &:-webkit-autofill {
      box-shadow: 0 0 0 20px var(--gray) inset;
      -webkit-text-fill-color: white;
      color: white;
    }
  }
`;

const InfoBlock = styled.div`
  display: flex;
  flex-direction: row;
  padding: 5px;
  margin: 8px;
  > label:first-child {
    width: 120px;
    display: flex;
    justify-content: flex-end;
    align-items: flex-start;
    text-shadow: white 0 0 5px;
    margin-right: 10px;
    margin-top: 5px;
    margin-left: 20px;
  }
  > button {
    border: 1px solid white;
    color: white;
    border-radius: 5px;
    padding: 0 0.5rem;
    background-color: var(--gray);
    margin: 0 2rem;
    &:hover {
      transition: 0.2s ease-in-out;
      text-shadow: white 0 0 5px;
      background-color: var(--neon-yellow);
      color: black;
      border: 1px solid var(--neon-yellow);
      cursor: pointer;
    }
  }
  #map {
    width: 20rem;
    height: 21rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-bottom: 1rem;
    margin-left: 0.8rem;
    > #locationButton {
      padding: 1rem 2rem;
      background-color: var(--gray);
      &:hover {
        transition: 0.2s ease-in-out;
        text-shadow: white 0 0 5px;
        background-color: var(--neon-yellow);
        color: black;
        border: 1px solid var(--neon-yellow);
        cursor: pointer;
      }
    }
    > button {
      margin-top: 1rem;
      border: 1px solid white;
      border-radius: 15px;
      cursor: pointer;
      color: white;
    }
  }
  > div {
    display: flex;
    flex-direction: column;
    justify-content: center;
    > div {
      margin: 5px 5px 0px 0;
      font-size: 15px;
      > i {
        color: white;
        font-size: 16px;
        margin-left: 15px;
        &:hover {
          color: var(--neon-red);
          text-shadow: white 0 0 2px;
          transition: 0.2s ease-in-out;
          cursor: pointer;
        }
      }
    }
  }
`;

const Pfp = styled.img<PreviewPfp>`
  width: 150px;
  height: 150px;
  border: 2px solid white;
  border-radius: 100px;
  margin: 0 10px;
`;

const Button = styled.button`
  border: 1px solid white;
  border-radius: 10px;
  align-items: center;
  padding: 15px;
  margin: 15px 30px;
  font-size: 20px;
  height: 50px;
  text-align: center;
  display: flex;
  text-decoration: none;
  color: white;
  justify-content: center;
  background-color: var(--gray);
  cursor: pointer;
  i {
    padding-right: 10px;
  }
  &:hover {
    transition: 0.2s ease-in-out;
    text-shadow: white 0 0 5px;
    background-color: var(--neon-yellow);
    color: black;
    border: 1px solid var(--neon-yellow);
    cursor: pointer;
  }
`;

const TempButton = styled(Link)`
  border: 1px solid white;
  border-radius: 10px;
  align-items: center;
  padding: 15px;
  margin: 15px 30px;
  font-size: 20px;
  height: 50px;
  text-align: center;
  display: flex;
  text-decoration: none;
  color: white;
  justify-content: center;
  i {
    padding-right: 10px;
  }
  &:hover {
    transition: 0.2s ease-in-out;
    text-shadow: white 0 0 5px;
    background-color: var(--neon-yellow);
    color: black;
    border: 1px solid var(--neon-yellow);
    cursor: pointer;
  }
`; // 임시 버튼, 버튼에 기능 넣으면 navigate 쓰고 Button으로 통일하자

const NoLinkButton = styled.button`
  border: 1px solid white;
  border-radius: 10px;
  align-items: center;
  margin: 3px 0px 15px 20px;
  font-size: 14px;
  text-align: center;
  display: flex;
  color: white;
  justify-content: center;
  align-items: center;
  text-align: center;
  height: 35px;
  background-color: var(--gray);
  padding: 5px 10px;
  i {
    padding-right: 5px;
  }
  &:hover {
    transition: 0.2s ease-in-out;
    text-shadow: white 0 0 5px;
    background-color: var(--neon-yellow);
    color: black;
    border: 1px solid var(--neon-yellow);
    cursor: pointer;
  }
`;

const InputButton = styled.label`
  border: 1px solid white;
  border-radius: 10px;
  align-items: center;
  margin: 3px 0px 15px 20px;
  font-size: 14px;
  height: 35px;
  text-align: center;
  display: flex;
  color: white;
  justify-content: center;
  align-items: center;
  text-align: center;
  background-color: var(--gray);
  padding: 5px 10px;
  cursor: pointer;
  > input {
    display: none;
  }
  i {
    padding-right: 5px;
  }
  &:hover {
    transition: 0.2s ease-in-out;
    text-shadow: white 0 0 5px;
    background-color: var(--neon-yellow);
    color: black;
    border: 1px solid var(--neon-yellow);
    cursor: pointer;
  }
`;

const TagContainer = styled.div`
  fieldset {
    display: flex;
    flex-direction: row;
    width: 25rem;
    flex-wrap: wrap;
    border: none;
    padding-left: 0;
    margin-left: 0;
  }
`;

interface PreviewPfp {
  src: string;
}

