const EditUser = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  // });
  const [img, setImg] = useState<any>(
    'https://cdn.discordapp.com/attachments/1030817860047618119/1030866099694211203/BackgroundEraser_20221016_002309876.png',
  );
  const schema = yup.object().shape({
    nickname: yup
      .string()
      .required('Please enter your nickname')
      .min(2)
      .max(24),
    phone: yup.number().required('Please enter digits only'),
    curPassword: yup
      .string()
      .min(
        6,
        'Passwords must be at least 6 characters, and contain one special character',
      )
      .max(24)
      .required('Enter your current password'),
    newPassword: yup
      .string()
      .min(
        6,
        'Passwords must be at least 6 characters, and contain one special character',
      )
      .max(24)
      .required('Enter your new password'),
    newPasswordCheck: yup
      .string()
      .required('Type your password again')
      .oneOf([yup.ref('newPassword')], 'Passwords must match'),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<UserFormInput>({
    resolver: yupResolver(schema),
    defaultValues: {
      // 초기값 설정
      nickname: 'nickname',
      phone: '01012345678',
      curPassword: 'currentPass',
      newPassword: 'newPass',
      newPasswordCheck: 'newPass',
    },
  });
  const onSubmitHandler = (data: UserFormInput) => {
    console.log('data is ', data);
    navigate('/members/mypage/1');
  };
  // const onInvalid = (errors: any) => console.error(errors);
  const onChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    console.log('change', event.target.value);
  };
  const [location, setLocation] = useState<{
    latitude: number;
    longitude: number;
  } | null>(null);

  useCurrentLocation().then((res) => {
    if (res === undefined) return;
    setLocation(res);
  });

  // const imgRef = useRef<any>();
  // function readImage(input: any) {
