  }
`;

const FreeDetail = () => {
  const { freeId } = useParams();
  const [post, setPost] = useState<FreeDataProps | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    axios
      .get(`/freeboards/${freeId}`)
      .then((res) => {
        setPost(res.data.data);
        console.log(post);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <FDContainer>
      {!isLoading ? (
        <BoardContainer>
          <div>
            {/* {post?.category} */}
            <i className="fa-solid fa-dumbbell" />
            운동
          </div>
          <h1>{post?.freeTitle}</h1>
          <div>
            <div>
              <i className="fa-regular fa-clock" />
              {timeDifference(`${post?.createdAt}`)}
            </div>
            <CountContainer>
              <div>
                <i className="fa-solid fa-eye view" />
                {post?.views}
              </div>
              <div>
                <i className="fa-regular fa-thumbs-up like" />
                {post?.likes}
              </div>
              <div>
                <i className="fa-regular fa-comment-dots comment" />
                {0}
                {/* <div>{post?.freeComments.length}</div> */}
              </div>
            </CountContainer>
          </div>
          <CreatorCard memberId={1} nickname="aaa" heart={100} />
          <ContentContainer>
            {/* <div>
              <img src={preview} alt="preview" />
            </div> */}
            <div className="body">
              {post?.freeBody}
              안녕안녕안녕안녕 하세요하세요 안녕안녕안녕안녕 하세요하세요
              안녕안녕안녕안녕 하세요하세요 안녕안녕안녕안녕 하세요하세요
              안녕안녕안녕안녕 하세요하세요 안녕안녕안녕안녕 하세요하세요
            </div>
            <div>
              {/* 위치 */}
              <div className="map">
                <KakaoMap
                  latitude={37.7424074}
                  longitude={127.042215}
                  overlayvalue="운동 장소"
                />
              </div>
            </div>
          </ContentContainer>
          <div className="btnCon">
            <Button
              value="좋아요"
              onClick={() => console.log('좋아요!')}
              icon={<i className="fa-solid fa-heart" />}
            />
            <div>
              <Button
                value="수정"
                onClick={() => console.log('수정')}
                icon={<i className="fa-solid fa-pen-to-square" />}
              />
              <Button
                value="삭제"
                onClick={() => console.log('수정')}
                icon={<i className="fa-solid fa-trash" />}
              />
            </div>
          </div>
          <div className="commentCount">
            {0}
            개의 댓글이 있습니다
          </div>
          {post?.freeComments &&
            post?.freeComments.map((el) => (
              <CommentBox
                key={el.commentId}
                memberId={el.memberId}
                data={el}
                board="freeboards"
                boardId={post.freeId}
              />
            ))}
          <CommentSubmitBox submitComment={`/freeboards/${post?.freeId}`} />
        </BoardContainer>
      ) : (
        <Loading />
      )}
    </FDContainer>
  );
};

export default FreeDetail;
