const ErrorMessage = styled.span`
  color: red;
  position: absolute;
  top: calc(100% + 5px);
  left: 0;
  font-size: 12px;
`;

interface RecruitFormInput {
  recruitTagDtos: { tagId: number; tagName: string; emoji: string }[];
  title: string;
  body: string;
  date: string;
  require: number;
  minRequire: number;
  location: string;
  lat: number;
  lon: number;
  sex: 'Both' | 'Male' | 'Female';
  ages: number[];
  heartLimit: number;
  // image: string;
  tagSearch: string;
}

const KakaoMapForClick = ({
  control,
  setValue,
  currentLat,
  currentLon,
}: any) => {
  const lat = useWatch({
    control,
    name: 'lat',
    defaultValue: currentLat,
  });
  const lon = useWatch({
    control,
    name: 'lon',
    defaultValue: currentLon,
  });
  console.log(lat, lon);
  return <KakaoMapClick latitude={lat} longitude={lon} setValue={setValue} />;
};

const CreateRecruit = () => {
  const accessToken = useSelector((state: any) => state.accessToken);
  const refreshToken = useSelector((state: any) => state.refreshToken);
  const memberId = useSelector((state: any) => state.memberId);
  const navigate = useNavigate();
  const {
    register,
    control,
    handleSubmit,
    getValues,
    setValue,
    formState: { errors },
  } = useForm<RecruitFormInput>();
  const { fields, append, remove } = useFieldArray({
    control,
    name: 'recruitTagDtos',
    rules: {
      validate: {
        moreThanOneTag: (values) =>
          values.length > 0 ? true : 'ÌÉúÍ∑∏Îäî 1Í∞ú Ïù¥ÏÉÅ ÏÑ†ÌÉùÌï¥Ïïº Ìï©ÎãàÎã§',
      },
    },
  });

  const onSubmit = (data: RecruitFormInput) => {
    // tagSearchÎäî postBodyÏóêÏÑú Ï†úÏô∏Ìï®.
    const { tagSearch, ...postBody } = data;
    axios
      .post(
        `${process.env.REACT_APP_API_URL}/recruits`,
        {
          memberId,
          ...postBody,
        },
        {
          headers: {
            Authorization: accessToken,
            Refresh: refreshToken,
          },
        },
      )
      .then((res) => {
        console.log(res);
        navigate(`/recruits`);
      })
      .catch((err) => console.log(err));
  };
  const { location } = useCurrentLocation();
  console.log(location);
  const TAG_DATA = [
    { tagId: 1, tagName: 'Ï∂ïÍµ¨/ÌíãÏÇ¥', emoji: '‚öΩÔ∏è' },
    { tagId: 2, tagName: 'ÎÜçÍµ¨', emoji: 'üèÄ' },
    { tagId: 3, tagName: 'ÏïºÍµ¨', emoji: '‚öæÔ∏è' },
    { tagId: 4, tagName: 'Î∞∞Íµ¨', emoji: 'üèê' },
    { tagId: 5, tagName: 'Î≥µÏã±', emoji: 'ü•ä' },
    { tagId: 6, tagName: 'ÌÉÅÍµ¨', emoji: 'üèì' },
    { tagId: 7, tagName: 'Î∞∞ÎìúÎØºÌÑ¥', emoji: 'üè∏' },
    { tagId: 8, tagName: 'ÌÖåÎãàÏä§/Ïä§ÏøºÏãú', emoji: 'üéæ' },
    { tagId: 9, tagName: 'ÌÉúÍ∂åÎèÑ/Ïú†ÎèÑ', emoji: 'ü•ã' },
    { tagId: 10, tagName: 'Í≤ÄÎèÑ', emoji: '‚öîÔ∏è' },
    { tagId: 11, tagName: 'Î¨¥Ïà†/Ï£ºÏßìÏàò', emoji: 'ü•ã' },
    { tagId: 12, tagName: 'Ï°±Íµ¨', emoji: '‚öΩÔ∏è' },
    { tagId: 13, tagName: 'Îü¨Îãù', emoji: 'üèÉ' },
    { tagId: 14, tagName: 'ÏûêÏ†ÑÍ±∞', emoji: 'üö¥' },
    { tagId: 15, tagName: 'Îì±ÏÇ∞', emoji: 'üèîÔ∏è' },
    { tagId: 16, tagName: 'ÌÅ¥ÎùºÏù¥Î∞ç', emoji: 'üßó‚Äç‚ôÄÔ∏è' },
    { tagId: 17, tagName: 'ÏàòÏòÅ', emoji: 'üèä‚Äç‚ôÄÔ∏è' },
    { tagId: 18, tagName: 'Í≥®ÌîÑ', emoji: '‚õ≥Ô∏è' },
    { tagId: 19, tagName: 'ÏöîÍ∞Ä/ÌïÑÎùºÌÖåÏä§', emoji: 'üßò' },
    { tagId: 20, tagName: 'Ìó¨Ïä§/ÌÅ¨Î°úÏä§Ìïè', emoji: 'üèãÔ∏è' },
    { tagId: 21, tagName: 'Ïä§ÏºÄÏù¥Ìä∏/Ïù∏ÎùºÏù∏', emoji: '‚õ∏Ô∏è' },
  ];

  return (
    <RecruitFormContainer>
      <h1>Î™®Ïßë Í≤åÏãúÍ∏Ä ÏûëÏÑ±ÌïòÍ∏∞</h1>
      <span>ÏßÅÏ†ë Î™®ÏûÑÏùÑ ÎßåÎì§Ïñ¥ Î≥¥ÏÑ∏Ïöî!</span>

      <RecruitForm onSubmit={handleSubmit(onSubmit)}>
        <table>
          <tbody>
            <tr>
              <td>ÌÉúÍ∑∏</td>
              <td>
                <AutoCompleteForArray
                  fields={fields}
                  append={append}
                  remove={remove}
                  register={register}
                  control={control}
                  data={TAG_DATA}
                  tagLength={1}
                />
