                  type="text"
                  placeholder="Ïòà) 000Ïπ¥Ìéò, 00Íµê Îã§Î¶¨ ÏúÑ Ï§ëÍ∞Ñ ÏóòÎ¶¨Î≤†Ïù¥ÌÑ∞ Ïïû"
                  {...register('location', {
                    required: 'Î™®ÏûÑ Ïû•ÏÜåÎäî ÌïÑÏàòÌï≠Î™©ÏûÖÎãàÎã§',
                  })}
                />
                <ErrorMessage>{errors?.location?.message}</ErrorMessage>
              </td>
            </tr>
            <tr className="mapCon">
              <td>
                <label htmlFor="latlon">Ïπ¥Ïπ¥Ïò§Îßµ</label>
              </td>
              <td>
                {/* <div className="mapClick">
                  <KakaoMapClick
                    latitude={latlon.latitude}
                    longitude={latlon.longitude}
                  />
                </div> */}
                {location && (
                  <KakaoMapForClick
                    control={control}
                    setValue={setValue}
                    currentLat={location.latitude}
                    currentLon={location.longitude}
                  />
