import { useForm } from 'react-hook-form';
// import { useRef, useState } from 'react';
import styled from 'styled-components';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useEffect, useState } from 'react';
// import UseAutosizeTextArea from '../components/UseAutosaveTextArea';

interface FormInputFree {
  category: '질문' | '정보' | '나눔' | '운동';
  title: string;
  content: string;
  // image: string;
  location: string;
  tag: { tagId: number; tagName: string }[];
}

interface TagForm {
  tagId: number;
  categoryExercise: boolean;
  tagName: string;
  // "emoji":
  recruitCount: number;
  freeCount: number;
}

const Background = styled.div`
  background-color: var(--gray);
  color: white;
  display: flex;
  justify-content: center;
  padding: 150px 100px;
  height: 100%;
`;

const WarnSet = styled.div`
  display: flex;
  flex-direction: column;
  > span {
    color: var(--neon-red);
    font-size: 10px;
    padding: 0.5rem 0;
    margin-left: 1.2rem;
    > i {
      margin-right: 0.3rem;
    }
  }
`;

const CRForm = styled.form`
  width: auto;
  height: auto;
  border: 1px solid white;
  border-radius: 5px;
  padding: 10px 50px 30px 50px;
  margin: 30px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  .tagContainer {
    display: flex;
    flex-direction: column;
    > span {
      padding-left: 1.4rem;
      padding-top: 0.3rem;
      font-size: 12px;
      color: lightgrey;
    }
  }

  input,
  textarea,
  select {
    // margin-bottom: 15px;
    background-color: var(--gray);
    padding: 5px;
    margin-left: 20px;
    font-size: 14px;
    border: none;
    border: 1px solid gray;
    width: 15rem;
    outline: none;
    color: white;
    &:focus-within {
      border: 2px solid white;
      transition: 0.2s ease-in-out;
    }
    &:-webkit-autofill {
      box-shadow: 0 0 0 20px var(--gray) inset;
      -webkit-text-fill-color: white;
      color: white;
    }
  }

  > div {
    width: 100%;
    display: flex;
    align-items: flex-start;
    justify-content: flex-start;
    margin-top: 1.2rem;
    > label,
    .label {
      width: 50px;
      text-align: right;
      padding-top: 5px;
      text-shadow: white 0 0 3px;
    }
    .label {
      padding-top: 5px;
    }
    .select-container {
      position: relative;
      select {
        display: none;
      }
    }
  }
  .length {
    height: 300px;
  }
  input[type='file'] {
    display: none;
  }
  .imagebutton {
    border: 2px solid white;
    margin-left: 25px;
    width: 180px;
    border-radius: 10px;
    margin-bottom: 30px;
    margin-top: 5px;
    text-align: center;
    padding: 5px 10px;
    &:hover {
      background-color: var(--neon-yellow);
      color: black;
      border: 2px solid var(--neon-yellow);
      transition: 0.2s ease-in-out;
      cursor: pointer;
    }
  }

  > div:first-child {
    text-shadow: white 0 0 5px;
    font-size: 20px;
    text-align: center;
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 30px;
  }
  > div:last-child {
    display: flex;
    justify-content: center;
    align-items: center;
  }
  > label,
  .label {
    display: flex;
    justify-content: flex-start;
    align-items: flex-start;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  flex-direction: row;
  button {
    margin: 0px 30px 10px 30px;
    padding: 9px 15px;
    background-color: var(--gray);
    color: white;
    border: 2px solid white;
    border-radius: 5px;
    &:hover {
      background-color: var(--neon-yellow);
      color: black;
      border: 2px solid var(--neon-yellow);
      transition: 0.2s ease-in-out;
      cursor: pointer;
    }
  }
`;

const CreateFreeboard = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<FormInputFree>();
  const navigate = useNavigate();
  // const [warning, setWarning] = useState('');
  // const [content, setContent] = useState('');
  const [addedTags, setAddedTags] = useState<TagForm[]>([]);
  useEffect(() => {
    const getOriginalPost = () => {
      axios
        .get(`${process.env.REACT_APP_API_URL}/tags/freeboards?page=1&size=100`)
        .then((res: any) => {
          setAddedTags(res.data.data);
          console.log(addedTags);
        })
        .catch((err: any) => console.log(err));
    };
    getOriginalPost();
  }, []);

  const onSubmit = (data: FormInputFree) => {
    console.log({
      freeTitle: data.title,
      freeBody: data.content,
      category: data.category,
      location: data.location,
      freeTagDtos: [{ tagId: 1, tagName: '축구' }],
      memberId: 1,
    });
    axios
      .post(
        `${process.env.REACT_APP_API_URL}/freeboards`,
        {
          freeTitle: data.title,
          freeBody: data.content,
          category: data.category,
          location: data.location,
          freeTagDtos: [{ tagId: 1, tagName: '축구' }],
          memberId: `${localStorage.getItem('memberId')}`,
          // 태그와 멤버아이디가 고정되어있음
          // tagList: tags.reduce((r, e) => {
          //   r.push({ tagId: e.tagId });
          //   return r;
          // }, []),
          // tag, image 서버에 추가되면 그냥 data로 넣으면 될듯
        },
        {
          headers: {
            Authorization: `${localStorage.getItem('AccessToken')}`,
            Refresh: `${localStorage.getItem('RefreshToken')}`,
          },
        },
      )
      .then((res) => {
        console.log(res);
        navigate('/freeboards');
      })
      .catch((err) => {
        console.log(err);
        navigate('/login');
      });
    return false;
  };
  // const textAreaRef = useRef<HTMLTextAreaElement>(null);

  // UseAutosizeTextArea(textAreaRef.current, content);

