import { useForm } from 'react-hook-form';
import { useState } from 'react';
import styled from 'styled-components';
import TagAutoComplete from '../components/TagAutoComplete';

enum GenderEnum {
  female = '여성',
  male = '남성',
}

enum AgeEnum {
  teenage = '10대',
  twenties = '20대',
  thirties = '30대',
  forties = '40대',
  fifties = '50대',
  sixties = '60대',
}

interface IFormInput {
  tag: string;
  title: string;
  content: string;
  date: string;
  quota: number;
  location: string;
  genderCondition: GenderEnum;
  ageCondition: AgeEnum;
  heartRateCondition: number;
  image: string;
}

const CRContainer = styled.div`
  background-color: var(--gray);
  color: white;
  display: flex;
  justify-content: center;
  margin-top: 100px;
  height: 100%;
`;

const CRForm = styled.form`
  width: auto;
  height: auto;
  border: 1px solid white;
  border-radius: 5px;
  padding: 20px;
  margin: 30px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  label,
  input,
  textarea,
  select {
    border-radius: 5px;
    border: none;
    width: 400px;
    height: 30px;
  }

  input,
  textarea,
  select {
    margin-bottom: 10px;
  }

  .length {
    height: 100px;
  }
`;

const CreateRecruit = () => {
  const { register, handleSubmit } = useForm<IFormInput>();
  const onSubmit = (data: IFormInput) => console.log(data);
  const [filterTag, setFilterTag] = useState('');

  return (
    <CRContainer>
      <CRForm onSubmit={handleSubmit(onSubmit)}>
        <div>모집 게시글 생성</div>
        <label htmlFor="tag">태그</label>
        <TagAutoComplete filterTag={filterTag} setFilterTag={setFilterTag} />
        <label htmlFor="title">제목</label>
        <input
          id="title"
          type="text"
          {...register('title', { required: true })}
        />
        <label htmlFor="content">내용</label>
        <textarea
          id="content"
          className="length"
          {...register('content', { required: true })}
        />
        <label htmlFor="date">모임 일시</label>
        <input
          id="date"
          type="datetime-local"
          {...register('date', { required: true })}
        />
        <label htmlFor="quota">모임 인원</label>
        <input
          id="quota"
          type="number"
          {...register('quota', { required: true })}
        />
        <label htmlFor="location">모임 장소</label>
        <input
          id="location"
          type="text"
          {...register('location', { required: true })}
        />
        <label htmlFor="genderCondition">성별 조건</label>
        <select id="genderCondition" {...register('genderCondition')}>
          <option value="female">여성</option>
          <option value="male">남성</option>
        </select>
        <label htmlFor="ageCondition">나이대 조건</label>
        <input
          type="range"
          name="age"
          id="ageCondition"
          min="10"
          max="60"
          step="10"
        />
        <output name="x" htmlFor="ageCondition" />
        <label htmlFor="heartRateCondition">심박수 조건</label>
        <input
          id="heartRateCondition"
          type="range"
          {...register('heartRateCondition')}
        />
        <label htmlFor="image">이미지</label>
        <input id="image" type="file" {...register('image')} />
        <button type="submit">작성하기</button>
      </CRForm>
    </CRContainer>
  );
};

export default CreateRecruit;
