interface ReviewConditionProps {
  recruitStatus: '모집중' | '최소인원충족' | '모집완료' | '활동종료';
  applies: { memberId: number; nickname: string; heart: number }[];
  minRequire: number;
  require: number;
  applicantsId: number[];
  creatorId: number;
  reviews: {
    reviewId: number;
    memberId: number;
    nickname: string;
    heart: number;
    body: string;
    star: number;
  }[];
  creatorNickname: string;
  setData: any;
}

const RecruitApplyAfterMeeting = ({
  recruitStatus,
  applies,
  minRequire,
  require,
  applicantsId,
  creatorId,
  reviews,
  creatorNickname,
  setData,
}: ReviewConditionProps) => {
  const { recruitId } = useParams();
  const [reviewModal, setReviewModal] = useState<boolean>(false);
  const LOGIN_ID = Number(localStorage.getItem('memberId')) || -1;
  const REVIEW_STAR_NUM = reviews.reduce((res: number[], ele) => {
    res.push(ele.star);
    return res;
  }, []);

  const REVIEW_STAR_AVER = (
    REVIEW_STAR_NUM.reduce((r, e) => r + e, 0) / REVIEW_STAR_NUM.length
  ).toFixed(2);

  const REVIEW_ID = reviews.reduce((res: number[], ele) => {
    res.push(ele.memberId);
    return res;
  }, []);

  return (
    <SelectContainer>
      {reviewModal ? (
        <RecruitReviewModal
          creatorId={creatorId}
          creatorNickname={creatorNickname}
          applies={applies}
          setReviewModal={setReviewModal}
          setData={setData}
        />
      ) : (
        ''
      )}
      <SelectBox>
        <h3>
          <span>모집인원</span>
        </h3>
        <ApplicantsBox>
          <div style={{ color: classifyingStatus(recruitStatus).color }}>
            <i className={classifyingStatus(recruitStatus).icon} />
            {recruitStatus}
          </div>
          <h2>
            {`${applies.length}`}
            <span>{`/${require}명`}</span>
          </h2>
          <div>{`(최소인원 ${minRequire}명)`}</div>
          <div>
            {applies.map((el) => (
              <ProfileImg key={el.memberId}>
                <img
                  src={`https://picsum.photos/seed/${el.memberId}/20/20.webp`}
                  alt={`avatar of ${maskingNickname(el.nickname)}`}
                />
                <Bubble>
                  <div className="bubble">{maskingNickname(el.nickname)}</div>
                </Bubble>
              </ProfileImg>
            ))}
          </div>
        </ApplicantsBox>
        <div />
      </SelectBox>
      {[...applicantsId, creatorId].includes(LOGIN_ID) &&
      recruitStatus !== '모집중' ? (
        <SelectBox>
          {['최소인원충족', '모집완료'].includes(recruitStatus) ? (
            <>
              <h3>
                <span>활동종료변경</span>
              </h3>
              <ApplyBox>
                <div>활동이 종료되었나요?</div>
                <Button
                  value="활동 종료"
                  onClick={() => {
                    console.log(`PATCH /recruits/${recruitId}/status`);
                    axios
                      .patch(
                        `${process.env.REACT_APP_API_URL}/recruits/${recruitId}/status`,
                        {},
                        {
                          headers: {
                            Authorization: localStorage.getItem('AccessToken'),
                            Refresh: localStorage.getItem('RefreshToken'),
                          },
                        },
                      )
                      .then((res) => {
                        console.log(res.data.data);
                        setData(res.data.data);
                      })
                      .catch((err) => console.log(err));
                  }}
                />
              </ApplyBox>
            </>
          ) : (
            <>
              <h3>
                <span>리뷰작성</span>
              </h3>
              <ApplyBox>
                {!REVIEW_ID.includes(LOGIN_ID) ? (
                  <>
                    <div>리뷰를 작성하시겠습니까?</div>
                    <Button
                      value="리뷰 작성"
                      onClick={() => setReviewModal(true)}
                    />
                  </>
                ) : (
                  <>
                    <div>리뷰 작성이 완료되었습니다</div>
                    <Button value="리뷰 완료" disabled onClick={() => {}} />
                  </>
                )}
              </ApplyBox>
            </>
          )}
        </SelectBox>
      ) : (
        <SelectBox>
          <h3>
            <span>모집신청</span>
          </h3>
          <ApplyBox>
            <div>활동이 종료되었습니다</div>
            <Button value="신청 불가" disabled onClick={() => {}} />
          </ApplyBox>
        </SelectBox>
      )}
      {recruitStatus === '활동종료' ? (
        <SelectBox>
          <h3>
            <span>리뷰내역</span>
          </h3>
          <ReviewBox>
            <div>
              {reviews.length ? (
                <>
                  <i className="fa-solid fa-star" />
                  <span>{`${REVIEW_STAR_AVER}`}</span>
                  <span>{`총 ${reviews.length}개의 리뷰`}</span>
                </>
              ) : (
                <span>아직 리뷰가 작성되지 않았습니다</span>
              )}
            </div>
            <ul>
              {reviews.map((el) => (
                <li key={el.reviewId}>
                  <Link to={`/members/${el.memberId}`}>
                    <img
                      src={`https://picsum.photos/seed/${el.memberId}/50/50.webp`}
                      alt={`avatar of ${el.nickname}`}
                    />
                    <div>
                      <div>
                        <span>{el.nickname}</span>
                        <span>
                          <i className="fa-solid fa-heart" />
                          {el.heart}
                        </span>
                      </div>
                      <div>
                        {[...Array(el.star)].map((e, i) => (
                          <i className="fa-solid fa-star" key={i + 1} />
                        ))}
                      </div>
                    </div>
                  </Link>
                  {el.body}
                </li>
              ))}
            </ul>
          </ReviewBox>
        </SelectBox>
      ) : (
        <SelectBox>
          <h3>
            <span>유의사항</span>
          </h3>
          <div>
            <ul>
              <li>조건이 충족되지 못하면 신청이 불가합니다</li>
              <li>모집이 완료된 뒤에는 모임 전이라도 취소가 불가합니다</li>
              <li>신중한 모임 참가 부탁드릴게요!</li>
            </ul>
          </div>
        </SelectBox>
      )}
    </SelectContainer>
  );
};

export default RecruitApplyAfterMeeting;

